import './App.css';
import 'animate.css';
import React, {useEffect} from 'react';
import handleViewport from 'react-in-viewport';

const App = (props) => {


    const Block = (props) => {
        const {inViewport, forwardedRef, enterCount, leaveCount} = props;

        useEffect(() => {
            console.log(props)
        }, [props])

        return (
            <div className="App">
                <header className="App-header">
                    <div className="bouncing-h1">
                        <h1>An animated element</h1>
                        <p>{`Enter viewport: ${enterCount} times`}</p>
                        <p>{`Leave viewport: ${leaveCount} times`}</p>
                        <p>{`In viewport: ${inViewport}`}</p>
                    </div>
                </header>
                <header className="App-header">
                    <div>
                        <h1 className="bouncing-h1">An animated element</h1>
                    </div>
                </header>
                <header className="App-header">
                    <div>
                        <h1 className="bouncing-h1">An animated element</h1>
                    </div>
                </header>
            </div>
        );
    }

    const ViewportBlock = handleViewport(Block, /** options: {}, config: {} **/);

    return (
        <div>
            <div style={{height: '100vh'}}>
                <h2>Scroll down to make component in viewport</h2>
            </div>
            <ViewportBlock onEnterViewport={() => console.log('enter')} onLeaveViewport={() => console.log('leave')}/>
        </div>
    )
}

export default App;
