import 'animate.css';
import React, { useRef } from 'react';
import handleViewport from 'react-in-viewport';

const App = (props) => {
    const { inViewport, forwardedRef, enterCount , leaveCount } = props;

    return (
    <div className="App">
      <header className="App-header">
        <div className="bouncing-h1">
            <h1>An animated element</h1>
            <p>{`Enter viewport: ${enterCount} times`}</p>
            <p>{`Leave viewport: ${leaveCount} times`}</p>
            <p>{`In viewport: ${inViewport}`}</p>

        </div>
      </header>
        <header className="App-header">
            <div>
                <h1 className="bouncing-h1">An animated element</h1>
            </div>
        </header>
        <header className="App-header">
            <div>
                <h1 className="bouncing-h1">An animated element</h1>
            </div>
        </header>
    </div>
  );
}

export default App;
