import './App.css';
import 'animate.css';
import React, {useEffect, useState} from 'react';
import handleViewport from 'react-in-viewport';

const App = (props) => (
    <div>
        <div style={{height: '100vh'}}>
            <h2>Scroll down to make component in viewport</h2>
        </div>
        <ViewportBlock onEnterViewport={() => {return (<Block/>)}} onLeaveViewport={() => {return (<Block/>)}}/>
    </div>
)



const Block = (props) => {
    const {inViewport, forwardedRef, enterCount} = props;
    const [key , setKey] = useState(0);
    let num = 0

    const incrementKey = () =>{
        setKey(Math.random() * 10);
    }


    useEffect(() => {
        console.log("props in useEffect" , props)
        // setKey(incrementKey());
        incrementKey()
        console.log("key" , key)
        console.log("forwardedRef" , forwardedRef)
        // setKey(Math.random() * 10)
    }, [props])

    useEffect(() => {
        console.log("key" , key)
    }, [key])

    return (
        <div ref={forwardedRef} className="App" key={num}>
            <header className="App-header" key={num}>
                <div key={num}>
                    <h1 className="bouncing-h1-animation" key={num}>An animated element</h1>
                </div>
            </header>
        </div>
    )
}

const ViewportBlock = handleViewport(Block, /** options: {}, config: {} **/);


export default App;
