import './App.css';
import 'animate.css';
import React, {useEffect} from 'react';
import handleViewport from 'react-in-viewport';

const App = (props) => (
    <div>
        <div style={{height: '100vh'}}>
            <h2>Scroll down to make component in viewport</h2>
        </div>
        <ViewportBlock onEnterViewport={() => {return (<Block/>)}} onLeaveViewport={() => {return (<Block/>)}}/>
    </div>
)


const Block = (props) => {
    const {inViewport, forwardedRef, enterCount} = props;

    useEffect(() => {
        return(<Block/>)
    }, [props])

    if (inViewport && enterCount === 1) {
        return (
            <div ref={forwardedRef} className="App">
                <header className="App-header">
                    <div>
                        <h1 className="bouncing-h1-animation">An animated element</h1>
                        <h1 className="bouncing-h1-animation">{inViewport}</h1>
                    </div>
                </header>
                <header className="App-header">
                    <div>
                        <h1 className="bouncing-h1-animation">An animated element</h1>
                    </div>
                </header>
                {/*<header className="App-header">*/}
                {/*    <div>*/}
                {/*        <h1 className="bouncing-h1-animation">An animated element</h1>*/}
                {/*    </div>*/}
                {/*</header>*/}
            </div>
        );
    }
    return (
        <div ref={forwardedRef} className="App">
            <header className="App-header">
                <div>
                    <h1 className="bouncing-h1-animation">An animated element</h1>
                </div>
            </header>
        </div>
    )
}

const ViewportBlock = handleViewport(Block, /** options: {}, config: {} **/);


export default App;
